'PROCEDURE get_pdf ( ptable varchar2, ',
'                    playout VARCHAR2, ',
'                    pid VARCHAR2 default ''ALL'', ',
'                    pset setting, ',
'                    pdfid OUT INTEGER) AS',
'  ',
'  l_blob           BLOB;',
'  qid VARCHAR2(200);',
'  ',
'BEGIN',
'  ',
'    APEX_DEBUG.INFO (p_message => ''ws1 -pid=''||pid);',
'    DBMS_LOB.createtemporary(l_blob, FALSE);',
'    ',
'    tset := pset;       -- Get the parameters',
'',
'    if pid = ''ALL'' then qid := NULL;',
'    else',
'        qid := pid;',
'    end if;',
'    APEX_DEBUG.INFO (p_message => ''ws1 -qid=''||qid);',
