'',
'function do_query(pname varchar2, ',
'                tagname varchar2 default NULL, ',
'                pmode varchar2 default ''Q'') ',
'return varchar2 ',
'is',
'/* ---------------------------------------------------------',
' Get business object',
' Get foreign keys definitions from Oracle dictionnary ',
' and generates a sql query that will generate a XML document',
'-----------------------------------------------------------*/',
'    ind INTEGER := 0;',
'    lreq VARCHAR2(32767);',
'    NL CONSTANT VARCHAR2(10) := chr(13)||chr(10) ;',
'    TAB CONSTANT VARCHAR2(100) := chr(9);',
'    pk_colname VARCHAR2(100);',
'    type array_t is table of varchar2(100);',
'    array array_t := array_t(NULL);',
'    arraychild array_t := array_t(NULL);',
'    i INTEGER := 0;',
'    k integer := 0;',
'    ltab integer := 0;',
'    joinclause VARCHAR2(1000) := '''';',
'    joinchild VARCHAR2(1000) := '''';',
'    tname VARCHAR2(100);',
'    tag_name   VARCHAR2(100);',
'    tagset_name VARCHAR2(100);',
'    ispk INTEGER := 0;',
'    TSUFFIX CONSTANT VARCHAR2(30) := ''_FOP_V'';',
'',
'    TOO_LARGE EXCEPTION ;',
'',
'/*  ---------Cursor summary ------------------------',
'C1: Child tables',
'C2: List of columns for a given table',
'C3: Column in a primary key',
'C4: Master tables of the given table',
'C5: Master tables of a child table',
' ---------------------------------------------------*/',
'',
'-- -------------------------------------------------',
'-- retrieve all the child tables for a table name',
'-- NOT IMPLEMENTED because we can use a view ',
'-- instead with _FOP_V suffix and get better control',
'-- -------------------------------------------------',
'/*',
'CURSOR C1 (ptable VARCHAR2) IS SELECT',
'    "C"."CONSTRAINT_NAME",',
'    "C"."OWNER" AS "SCHEMA_NAME",',
'    "C"."TABLE_NAME",',
'    "COL"."COLUMN_NAME",',
'    "REF_COL"."OWNER" AS "REF_SCHEMA_NAME",',
'    "REF_COL"."TABLE_NAME" AS "REF_TABLE_NAME",',
'    "REF_COL"."COLUMN_NAME" AS "REF_COLUMN_NAME"',
'FROM',
'    "USER_CONSTRAINTS" "C"',
'INNER JOIN "USER_CONS_COLUMNS" "COL" ON "COL"."OWNER" = "C"."OWNER"',
' AND "COL"."CONSTRAINT_NAME" = "C"."CONSTRAINT_NAME"',
'INNER JOIN "USER_CONS_COLUMNS" "REF_COL" ON "REF_COL"."OWNER" = "C"."R_OWNER"',
' AND "REF_COL"."CONSTRAINT_NAME" = "C"."R_CONSTRAINT_NAME"',
' AND "REF_COL"."POSITION" = "COL"."POSITION"',
'WHERE "REF_COL"."TABLE_NAME" = upper(ptable) AND "C"."CONSTRAINT_TYPE" = ''R'';',
'*/',
'-- If a view is named with suffix ''_FOP_V'', this one superseeds the table',
'CURSOR C1 (ptable VARCHAR2) IS SELECT',
'    C.CONSTRAINT_NAME,',
'    C.OWNER AS SCHEMA_NAME,',
'    C.TABLE_NAME,',
'    COL.COLUMN_NAME,',
'    REF_COL.OWNER AS REF_SCHEMA_NAME,',
'    REF_COL.TABLE_NAME AS REF_TABLE_NAME,',
'    REF_COL.COLUMN_NAME AS REF_COLUMN_NAME,',
'    U.REPORT_REGION_TITLE',
'FROM',
'    USER_CONSTRAINTS C,',
'    APEX_UI_DEFAULTS_TABLES U,',
'    USER_CONS_COLUMNS COL,',
'    USER_CONS_COLUMNS REF_COL',
'where COL.OWNER = C.OWNER and',
'COL.CONSTRAINT_NAME = C.CONSTRAINT_NAME and',
'REF_COL.OWNER = C.R_OWNER and',
'REF_COL.CONSTRAINT_NAME = C.R_CONSTRAINT_NAME and',
'REF_COL.POSITION = COL.POSITION and',
'REF_COL.TABLE_NAME = upper(ptable) AND C.CONSTRAINT_TYPE = ''R''',
'and C.TABLE_NAME = u.TABLE_NAME(+)',
'AND (C.TABLE_NAME LIKE ''%FOP_V'' or  (C.TABLE_NAME NOT LIKE ''%FOP_V'' and C.TABLE_NAME NOT IN ',
'(SELECT',
'        REPLACE("C"."TABLE_NAME", ''_FOP_V'','''') TABLE_NAME',
'    FROM',
'        "USER_CONSTRAINTS" "C"',
