'        else',
'            lreq := lreq ||'','' ||NL||gtab(ltab+3) ; ',
'        end if;',
'        if instr(lc2.DATA_TYPE,''TIMESTAMP'') >0 then -- ''TIMESTAMP(6) WITH LOCAL TIME ZONE'' then',
'            lreq := lreq || '' TO_CHAR(''||lc1.TABLE_NAME ||''.''||lc2.COLUMN_NAME ||'', ''''YY-MM-DD'''')''',
'            ||'' AS "'' || lc2.COLUMN_NAME ||''"'';',
'        else',
'            lreq := lreq || lc1.TABLE_NAME ||''.''||lc2.COLUMN_NAME ||'' AS "'' || lc2.COLUMN_NAME ||''"'';',
'        end if;',
'        ',
'        ind := 1;',
'    end loop;   -- end columns',
'    -- Add childs of child',
'    k := 0;',
'    joinchild := '''';',
'    for lC5 in C5(lc1.TABLE_NAME) loop',
'        if lc4.REF_TABLE_NAME != pname then     -- if it''s not a self relationship        ',
'            k := k +1;',
'            arraychild.extend();',
'            arraychild(k) := lC5.REF_TABLE_NAME;',
'            -- Add to join clause',
'            joinchild := joinchild || NL ||gtab(ltab)|| '' AND ''||',
'                lC5.TABLE_NAME ||''.''||lC5.COLUMN_NAME||''=''|| ',
'                lC5.REF_TABLE_NAME ||''.''||lC5.REF_COLUMN_NAME || NL; ',
'            for lc2 in C2(lC5.REF_TABLE_NAME) loop  ',
'                if ind = 1 then',
'                    lreq := lreq ||'','' || NL; ',
'                end if;',
'                lreq := lreq || gtab(ltab+3) ||lC5.REF_TABLE_NAME ||''.'' ||lc2.COLUMN_NAME ||',
'                     '' AS "'' ||lC5.REF_TABLE_NAME||''_''|| lc2.COLUMN_NAME ||''"'';',
'            end loop;',
'        end if;',
'    end loop;',
' ',
'    lreq := lreq || NL||gtab(ltab+2)|| '')''||NL||gtab(ltab+1)||'' )''||NL||gtab(ltab)||'' )''||',
'            NL||gtab(ltab) ||'' from '' || lc1.TABLE_NAME;',
'    -- References Table joined',
'    for j in 1..k loop',
'        lreq := lreq ||'',''||NL||gtab(ltab) || arraychild(j);',
'    end loop; ',
'  ',
'    lreq := lreq || NL ||gtab(ltab)||'' where '' || lc1.TABLE_NAME||''.''||lc1.COLUMN_NAME||',
'        '' = e.''||pk_colname; ',
'    lreq := lreq || gtab(ltab)|| joinchild;  ',
'    lreq := lreq || NL|| gtab(ltab)|| '') '' || lc1.TABLE_NAME ; ',
'    ',
'end loop;       -- end childs',
'APEX_DEBUG.INFO (p_message => ''do_query - end childs''); ',
'lreq := lreq ||NL||gtab(5)|| '' )''||NL||gtab(4)||'' )''||NL||gtab(3)||'' )''||NL',
'    ||gtab(2)||'' )''||NL||gtab(1)||'' ) AS "'' || tname ||''"''; ',
'lreq := lreq || NL || gtab(1) ||'' from "'' ||tname ||''" e '';',
'    ',
'-- References joined Tables',
'for j in 1..i loop',
'    lreq := lreq ||'','' ||NL||gtab(3) || array(j);',
'end loop; ',
'      ',
'lreq := lreq || NL ||'' where rownum<''||tset.maxrows;',
'if ispk = 1 then ',
'    lreq := lreq || ''and ',
' NVL(:PKID,e.''|| pk_colname ||'') = e.'' ||pk_colname;',
'end if;',
'-- Add join clause',
'lreq := lreq ||joinclause;',
'lreq := lreq || NL||'')'';',
'',
'return lreq;',
'end do_query;',
' ',
