'from FOP_FRAGMENTS',
'where NAME = ''GENERIC_CHILD_ROW'';',
' ',
'tcell := tchild_cell; ',
' ',
'theader := replace(theader,''#TAB#'', upper(tname)) ;',
'theader := replace(theader,''#COVER_TITLE#'', tset.title) ;',
'',
'-- adapting orientation',
'if tset.orientation = ''L'' then ',
'    theader := replace(',
'                theader, ',
'                ''page-height="297.0mm" page-width="210.0mm"'', ',
'                ''page-height="210.0mm" page-width="297.0mm"'');',
'    tcolmax := TCOLMAX_LANDSCAPE;',
'else',
'    tcolmax := TCOLMAX_PORTRAIT;',
'end if;',
'',
'-- init output',
'lreq := theader;  ',
'lreq := lreq ||NL|| ''<xsl:for-each select=".//'' || tname ||''">'' || NL;',
'lreq := lreq || ''<xsl:for-each select=".//'' || tag_name ||''">'' ||  NL ;',
'lreq := lreq || ''<fo:block padding-top="14mm"'';',
'if tset.break = ''Y'' then',
'    lreq := lreq ||'' page-break-before="always"  >'' ||NL;',
'else',
'    lreq :=lreq || ''>'' ||NL;',
'end if;',
' ',
'-- ----------------------------------------------------------',
'-- retrieves column names involved in the Primary Key',
'-- Limitations: Only ONE column in the PK',
'-- ----------------------------------------------------------',
'for lc3 in C3 (tname) loop',
'    ispk := 1;',
'    pk_colname := lc3.column_name;',
'end loop;',
'',
'i := 0; -- we reset the max number of master fields to be printed ',
'ltab := 4;',
'for lc2 in C2 (tname) loop',
