'                end if;',
'            end if;',
'        end loop;',
'    end if;',
'end loop;',
' ',
'-- ---------------------------------------------------------',
'-- Add childs tables/views',
'-- ---------------------------------------------------------',
' ',
'for lc1 in C1(pname)  loop          -- for each Child table',
'     -- Appends Child Fragment with the Child title',
'    lreq := lreq || NL||',
'        replace(',
'            replace(tchild_header, ',
'                ''#REC#'', ',
'                ''.//''||lc1.TABLE_NAME||''/''|| lc1.TABLE_NAME||''_''||lc1.REF_TABLE_NAME ||''_REC''',
'            ),',
'            ''#TITLE#'',',
'            lc1.REPORT_REGION_TITLE',
'        ) ;',
'    lreq := lreq ||NL|| tcheader;',
'    tcol := '''';',
'    ',
'    -- Step 1 - Build columns headers ,col description (width, etc.)',
