'            end if;',
'        end if;',
'    end loop;   -- end child columns',
'    ',
'    /*',
'    -- ========== TEST ',
'    -- idem masters of childs',
'    for lC5 in C5(lc1.TABLE_NAME) loop',
'      if lC5.TABLE_NAME != pname then     -- if it''s not a self relationship',
'        for lc2 in C2(lC5.REF_TABLE_NAME) loop  -- for each child ',
