'            end if;',
'        end loop;',
'       end if;',
'    end loop;   -- end child columns',
'    ',
'    ',
'-- END TEST',
' */',
'    -- closes the child table header',
'    lreq := lreq || NL|| ''</fo:table-row> </fo:table-header> '';',
'    lreq := lreq || NL|| ''<!--  end child table header -->''||NL;',
