'    ',
'    -- Step 2 -- Builds cells',
'    /*',
'    lreq := lreq ||NL||''<fo:table-row ',
'                border-bottom="0.5pt solid blue">'' ||NL;',
'    */',
'',
'    lreq := lreq ||NL||tcrow || NL;',
'    i := 0;',
'    for lc2 in C2(lc1.TABLE_NAME) loop  -- for each column',
'        i := i+1;',
'        if i <=  tcolmax then',
'            if lc2.COLUMN_NAME != lc1.COLUMN_NAME then  -- we exclude FK',
'                lreq := lreq || NL||replace(tcell, ''#ITEM#'', lc2.COLUMN_NAME) || NL;',
'            end if;',
'        end if;',
'    end loop;   -- end columns',
'    ',
'    -- Add masters of child within the max limit of columns tcolmax',
'    -- "i" stays at his previous value and we start from this value',
'/*',
'    -- idem masters of childs',
'    for lC5 in C5(lc1.TABLE_NAME) loop',
'      if lC5.TABLE_NAME != pname then     -- if it''s not a self relationship',
'        for lc2 in C2(lC5.REF_TABLE_NAME) loop  -- for each child ',
'          ',
'            i := i+1;',
'            if i <= tcolmax then',
'                if lc2.COLUMN_NAME != lc1.COLUMN_NAME then  -- we exclude FK',
'                    lreq := lreq || NL||replace(tcell, ''#ITEM#'', lc2.COLUMN_NAME) || NL;',
'                end if;',
'            end if;',
'        end loop;',
'       end if;',
'    end loop;   -- end child columns',
'*/',
'    lreq := lreq || tchild_footer;',
'',
'end loop;       -- end childs',
'',
'lreq :=lreq || NL || ''<!--  end child -->'' ||NL; ',
'lreq :=lreq || NL || ''</fo:block>'' ||NL;       -- closes the container with the break',
'lreq := lreq || ''</xsl:for-each>''||NL|| ''</xsl:for-each>'' ||NL;',
'lreq := lreq ||NL|| tfooter;',
' ',
'return lreq;',
' ',
'end; ',
' ',
'END;',
